import React, {useState} from 'react';
import Cell from './Cell';
import ColumnCursor from './ColumnCursor';

const GameBoard = () => {
    return (
        <>
            <ColumnCursor/>
            <div className='gameboard'>
                {
                    boardState.flat().map((cellState, index) => {
                        return (
                            <Cell 
                                key={index} 
                                cellState={cellState} 
                                cellIndex={index}
                            />
                        );
                    })
                }
            </div>
        </>
    );
};

export default GameBoard;
