import React, { useState } from 'react'

const ToggleButton = ({ToggleFunc, isTurnedOn}) => {
    return (
        <div className={`Toggle-btn ${isTurnedOn && "on-Toggle-btn"}`} onClick={ToggleFunc}>
            <div className='toggle-knob'/>
        </div>
    );
}

export default ToggleButton;
