import React from 'react'
import { useGameContext } from './Context';

const Cell = ({cellState, cellIndex}) => {
    const {colOfNewDisk, setColOfNewDisk} = useGameContext();
    const updateNewDiskCol = () => {
        setColOfNewDisk(cellIndex % 7);
    };

    return <div className='gamecell' onClick={updateNewDiskCol}>{cellState}</div>
}

export default Cell;
