import React, { useEffect } from 'react'
import { useGameContext } from './GameContext';

const ColumnCursor = ({newDiskCol}) => {

    // inject the animation style in the component
    // 
    const colCursorAnimationStyle = () => {
        console.log(document.getElementsByClassName("game-cell")[0].clientWidth);
        const colCursorStyle = {
            "borderColor": "black transparent transparent transparent",

        };
        return colCursorStyle;

    }

    return (
        <div 
        className='column-cursor' 
        style={colCursorAnimationStyle()}
        />
    );
}

export default ColumnCursor;
