import React from 'react'
import { useGameContext } from './GameContext';


const diskColourToRender = {
    0: "empty",
    1: "player-one",
    2: "player-two",
};

const Cell = ({cellState, cellIndex}) => {
    const {setColOfNewDisk, moveCounter, setMoveCounter} = useGameContext();
    const selectNewDiskCol = () => {
        console.log("cellIndex:", cellIndex)
        setColOfNewDisk(cellIndex % 7);
    };
    return (
        <div className='gamecell' onClick={selectNewDiskCol}>
            <div className={`disk ${diskColourToRender[cellState]}`}></div>
        </div>
    );
}

export default Cell;
