import React, { useEffect, useRef } from 'react'
import { useGameContext } from './GameContext';

const ColumnCursor = () => {
    const {currentCol} = useGameContext(); 
    const lastColCursor = useRef(0);
    const isInitialRender = useRef(true);

    useEffect(() => {
        if (!isInitialRender.current) {
            const colWidth = document
                            .getElementsByClassName("game-cell")[0]
                            .clientWidth;
            const widthOfGameboard = document
                            .getElementsByClassName("gameboard")[0]
                            .clientWidth;
            const cursorOffset = (widthOfGameboard - 7 * colWidth) / 2;
            const leftOffsetOfColCursor = cursorOffset + colCursor * colWidth;
            // cursor offset equals to the padding of the gameboard
            document.documentElement.style.setProperty('--col-cursor-finish', `${leftOffsetOfColCursor}px`);
            lastColCursor.current = colCursor;
        } else {
            isInitialRender.current = false;
        }
    }, [colCursor]);
    return (
        <div 
        className='column-cursor' 

        />
    );
}

export default ColumnCursor;
