import React, {useContext, useEffect, useRef, useState} from 'react';
import {
    isMoveLegal, 
    findRowOfNewDisk, 
    hasActivePlayerWon
} from './GameLogic';
const GameContext = React.createContext();

const initialBoardState = [];
for (let i = 0; i < 6; i++) {
    initialBoardState[i] = [];
    for (let j = 0; j < 7; j++) {
        initialBoardState[i][j] = 0;
    }
}

const GameContextProvider = ({children}) => {
    // the children being passed in is the app component!!!
    const [boardState, setBoardState] = useState(initialBoardState);
    const [activePlayerNum, setActivePlayerNum] = useState(1);
    const [colOfNewDisk, setColOfNewDisk] = useState(0);
    const [winningPlayerNum, setWinningPlayerNum] = useState(0);
    const [moveCounter, setMoveCounter] = useState(0);

    const didMount = useRef(false);
    useEffect(() => {
        if (didMount.current && isMoveLegal(boardState, colOfNewDisk)) {
            const rowOfNewDisk = findRowOfNewDisk(boardState, colOfNewDisk);
            const newBoardState = boardState;
            newBoardState[rowOfNewDisk][colOfNewDisk] = activePlayerNum;
            setBoardState(newBoardState);
            if (hasActivePlayerWon(
                boardState, 
                activePlayerNum, 
                [rowOfNewDisk, colOfNewDisk]
            )) { 
                setWinningPlayerNum(activePlayerNum);
                console.log(`Player ${activePlayerNum} won !!!`)
            } else {
                const nextActivePlayerNum = activePlayerNum === 1 ? 2 : 1;
                setActivePlayerNum(nextActivePlayerNum);
            }
        } else {
            didMount.current = true;
        }
    }, [moveCounter]);

    return <GameContext.Provider value={{
        boardState, 
        setBoardState,
        activePlayerNum,
        setActivePlayerNum,
        colOfNewDisk,
        setColOfNewDisk,
        moveCounter, 
        setMoveCounter
    }}> 
        {children}
    </GameContext.Provider>
};

const useGameContext = () => {
    return useContext(GameContext);
}

export {useGameContext, GameContextProvider, initialBoardState};
