import React from 'react'
import { useGameContext } from './Context';

const Cell = ({cellState, cellIndex}) => {
    const {setColOfNewDisk} = useGameContext();
    const updateNewDiskCol = () => {
        setColOfNewDisk(cellIndex % 7);
    };
    if (cellState === 0) {
        return <div className='gamecell' onClick={updateNewDiskCol}> 0</div>
    }
    if (cellState === 1) {
        return <div className='gamecell' onClick={updateNewDiskCol}> 1</div>
    }
    if (cellState === 2) {
        return <div className='gamecell' onClick={updateNewDiskCol}> 2</div>
    }
}

export default Cell;
